// import logo from './logo.svg';
import './App.css';
import Product from './components/product/product.js'
import Products from './components/products/products.js'
import Header from './components/header/header.js'
import Orders from './components/orders/orders.js';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Main from './components/main/main.js';
import Cart from './components/cart/cart.js';
import { useEffect, useState } from 'react';
import axios from 'axios'
import OnLoadingPersonsData from './components/on_load/on_load.js';


import React from 'react';
import { trackPromise, usePromiseTracker } from 'react-promise-tracker';

const area = 'products';

const App = () => {
    const { promiseInProgress } = usePromiseTracker({ area });
    const [ products, setProducts ] = useState(null);

    useEffect(() => {
      const apiUrl = 'http://127.0.0.1:8000/products';
      const instance = axios.create({
        baseURL: apiUrl,
        headers: {'Authorization': 'Token 048f016a3a1e9c783c23cc190e0eb9d2d391e929'},
      });

      trackPromise(
      instance.get().then((resp) => {
        const prods = resp.data
        setProducts(prods);
        console.log(resp.data)
      }));
      }, [setAppState]);

    return (
      <div className="App">
          <BrowserRouter>
            <Routes>
              <Route path="cart" element={
                <Main element={
                  <Cart/>
                }/>
              } />
              <Route path="orders" element={
                <Main element={
                  <Orders/>
                }/>
                } />
              <Route path="wish_list" element={
                <Main/>
              } />
              <Route path="products" element={
                <Main element={
                  promiseInProgress
                  ? <div>Подождите, данные загружаются!</div>
                  : <Products products={products} /> 
                }/>
              } />
            </Routes>
        </BrowserRouter>
      </div>
    );
  }





export default App;
