// import logo from './logo.svg';
import "./App.css";
import Products from "./components/products/products.js";
import Orders from "./components/orders/orders.js";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Main from "./components/main/main.js";
import Cart from "./components/cart/cart.js";
import React, { useEffect, useState } from "react";
import EmptyPage from "./components/empty_page/empty_page.js";
import WishList from "./components/wishlist/wishlist.js";
import Registration from "./components/registration/registration.js";
import Login from "./components/login/login.js";
import { trackPromise, usePromiseTracker } from "react-promise-tracker";
import axios from "axios";

const area = "isAuth";
const apiUrl = "http://127.0.0.1:8000";

const App = () => {
  const [isAuth, setIsAuth] = useState(null);
  const { promiseInProgress } = usePromiseTracker({ area });

  if (localStorage.length === 0) localStorage.setItem("token", "");

  useEffect(() => {
    const instance = axios.create({
      baseURL: apiUrl,
      headers: { Authorization: `Token ${localStorage.getItem("token")}` },
    });

    trackPromise(
      instance.get().then((resp) => {
        setIsAuth(true)
      })
      .catch(err => {
        console.log(err.status)
        if(err.status == 401){

        }
      })
    );
  }, [setIsAuth]);
  if (promiseInProgress) {
    return <h1>Loading...</h1>;
  } else if (isAuth) {
    return (
      <div className="App">
        <BrowserRouter>
          <Routes>
            <Route path="cart" element={<Main element={<Cart />} />} />
            <Route path="orders" element={<Main element={<Orders />} />} />
            <Route path="wish_list" element={<Main element={<WishList />} />} />
            <Route path="products" element={<Main element={<Products />} />} />
            <Route
              path="registration"
              element={<Registration></Registration>}
            ></Route>
            <Route path="login" element={<Login></Login>}></Route>
          </Routes>
        </BrowserRouter>
      </div>
    );
  } else {
    return (
      <BrowserRouter>
        <Navigate to="registration"></Navigate>
      </BrowserRouter>
    );
  }
};

export default App;
