import "./products.css";
import Product from "../product/product.js";
import { trackPromise, usePromiseTracker } from "react-promise-tracker";
import { useEffect, useState } from "react";
import axios from "axios";
import get_data from "../../utils.js";
import EmptyPage from "../empty_page/empty_page.js";

const area = "products";
const apiUrl = "http://127.0.0.1:8000/products";
const token = "048f016a3a1e9c783c23cc190e0eb9d2d391e929";

function Products() {
  const { promiseInProgress } = usePromiseTracker({ area });
  const [products, setProducts] = useState([]);

  useEffect(() => {
    get_data(trackPromise, apiUrl, token, setProducts);
  }, [setProducts]);

  if (!products || products.length == 0) {
    return <EmptyPage text="No products" />;
  } else if (promiseInProgress) {
    return <div>Подождите, данные загружаются!</div>;
  } else {
    return (
      <div className="products">
        <div className="pageTitle">PRODUCTS</div>
        <div className="productsPageContainer">
          <div className="productsContainer">
            {products.map((prod) => (
              <Product
                name={prod.name}
                price={prod.price}
                key={prod.id}
              ></Product>
            ))}
          </div>
          <div className="productsSortFilter"></div>
        </div>
      </div>
    );
  }
}

export default Products;
